import React from 'react';
import { motion } from 'framer-motion';
import { team } from '../../data/dummyData';

const About = () => {
  return (
    <section id="about" className="py-20 gradient-bg">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-center mb-16"
        >
          <h2 className="text-4xl font-bold text-gray-900 mb-4">
            About <span className="text-gradient">SignBridge</span>
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            We're on a mission to break down barriers in education and create inclusive learning environments for all students.
          </p>
        </motion.div>

        {/* Mission Statement */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          viewport={{ once: true }}
        >
          <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-xl p-8 md:p-12">
            <div className="grid lg:grid-cols-2 gap-12 items-center">
              <div>
                <h3 className="text-3xl font-bold text-gray-900 dark:text-white mb-6">
                  Our Mission
                </h3>
                <p className="text-lg text-gray-600 dark:text-gray-300 mb-6 leading-relaxed">
                  At SignBridge, we believe that every student deserves equal access to quality education. Our platform combines cutting-edge AI technology with deep understanding of accessibility needs to create a truly inclusive learning experience.
                </p>
                <p className="text-lg text-gray-600 dark:text-gray-300 mb-6 leading-relaxed">
                  We're committed to empowering deaf and hard-of-hearing students by providing real-time sign language translation, automatic note generation, and seamless classroom integration.
                </p>
                <div className="flex flex-wrap gap-4">
                  <div className="flex items-center space-x-2">
                    <div className="w-3 h-3 bg-green-500 rounded-full"></div>
                    <span className="text-gray-700 dark:text-gray-300">Accessibility First</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <div className="w-3 h-3 bg-blue-500 rounded-full"></div>
                    <span className="text-gray-700 dark:text-gray-300">AI-Powered</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <div className="w-3 h-3 bg-purple-500 rounded-full"></div>
                    <span className="text-gray-700 dark:text-gray-300">User-Centered</span>
                  </div>
                </div>
              </div>
              <div className="relative">
                <div className="bg-gradient-to-br from-primary-100 to-secondary-100 dark:from-primary-900/20 dark:to-secondary-900/20 rounded-xl p-8 text-center">
                  <div className="text-6xl mb-4">ðŸŽ¯</div>
                  <h4 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">Our Vision</h4>
                  <p className="text-gray-600 dark:text-gray-300">A world where education knows no barriers</p>
                </div>
                <div className="absolute -top-4 -right-4 bg-white dark:bg-gray-800 p-3 rounded-lg shadow-lg">
                  <div className="text-2xl">ðŸŒŸ</div>
                </div>
                <div className="absolute -bottom-4 -left-4 bg-white dark:bg-gray-800 p-3 rounded-lg shadow-lg">
                  <div className="text-2xl">ðŸ’¡</div>
                </div>
              </div>
            </div>
          </div>
        </motion.div>

        {/* Team Section */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.4 }}
          viewport={{ once: true }}
          className="mt-16"
        >
          <div className="text-center mb-12">
            <h3 className="text-3xl font-bold text-gray-900 dark:text-white mb-4">
              Meet Our <span className="text-gradient">Team</span>
            </h3>
            <p className="text-xl text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
              The passionate individuals behind SignBridge who are dedicated to making education accessible for everyone.
            </p>
          </div>

          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
            {[
              {
                name: "Dr. Sarah Chen",
                role: "CEO & Founder",
                bio: "Former special education teacher with 15+ years experience in accessibility technology.",
                avatar: "ðŸ‘©â€ðŸ«"
              },
              {
                name: "Marcus Rodriguez",
                role: "CTO",
                bio: "AI/ML expert specializing in computer vision and natural language processing.",
                avatar: "ðŸ‘¨â€ðŸ’»"
              },
              {
                name: "Emily Watson",
                role: "Head of Product",
                bio: "UX designer focused on creating intuitive experiences for diverse user needs.",
                avatar: "ðŸ‘©â€ðŸŽ¨"
              },
              {
                name: "David Kim",
                role: "Lead Engineer",
                bio: "Full-stack developer with expertise in real-time communication systems.",
                avatar: "ðŸ‘¨â€ðŸ”§"
